			case 'x':
				result = multiplicar_1(entero1,entero2,clnt);
				printf("\nEl resultado de la multiplicacion ha sido: %f",*result);
				break;
			case '/':
				result = dividir_1(entero1,entero2,clnt);
				printf("\nEl resultado de la division ha sido: %f",*result);
				break;
